import {
    Alert,
    AlertIcon,
    AlertTitle,
    AlertDescription,
    CloseButton,
} from '@chakra-ui/react';

interface AlertaProps {
    email: string;
    password: string;
    onClose: () => void;
}

export const Alerta = ({ email, password, onClose }: AlertaProps) => {
    return (
        <>
            <Alert
                status='success'
                variant='subtle'
                flexDirection='column'
                alignItems='center'
                justifyContent='center'
                textAlign='center'
                height='200px'
            >
                <AlertIcon boxSize='40px' mr={0} />
                <AlertTitle mt={4} mb={1} fontSize='lg'>
                    Cadastro Realizado!
                </AlertTitle>
                <AlertDescription maxWidth='sm'>
                    Obrigado pelo seu interesse. Seu email é : ${email}, sua Password é : ${password}
                </AlertDescription>
                {/* Adicionando o CloseButton para fechar o alerta */}
                <CloseButton
                    alignSelf='flex-start'
                    position='relative'
                    left={-1}
                    top={-1}
                    onClick={onClose}
                />
            </Alert>
        </>
    );
};
